import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      monsters : [
        {
          name: 'Saurabh',
          id: 'abc1'
        },
        {
          name: 'Shailvi',
          id: 'abc2'
        },
        {
          name: 'Raghav',
          id: 'abc3'
        },
        {
          name: 'Stuti',
          id: 'abc4'
        }
      ]
    }
  }
  render() {
    return(
      <div className="App">
        {this.state.monsters.map(
          (monster) => <div key={monster.id}><h1>{monster.name}</h1><p>{monster.id}</p></div> )
        }
      </div>
    )
  }
}

export default App;
